package logging;import org.slf4j.Logger;public class Slf4jLoggerAdapter implements AppLogger{    private final Logger delegate;    public Slf4jLoggerAdapter(Logger delegate) {        this.delegate = delegate;    }    @Override    public void info(String message) {        delegate.info(message);    }    @Override    public void info(String message, Object... args) {        delegate.info(message, args);    }    @Override    public void debug(String message, Object... args) {        delegate.debug(message, args);    }}