package oauth;import java.io.IOException;import java.io.InputStream;import java.nio.charset.StandardCharsets;public class ClasspathClientConfig implements ClientConfigProvider{    private final String resourceName;    public ClasspathClientConfig(String resourceName) {        this.resourceName = resourceName;    }    @Override    public String loadClientJson() throws ConfigException, IOException {        try {            InputStream in = getClass().getClassLoader().getResourceAsStream(resourceName);            if (in == null) {                throw new ConfigException("Resource not found on classpath: " + resourceName);            }            byte[] bytes = in.readAllBytes();            return new String(bytes, StandardCharsets.UTF_8);        } catch (Exception e) {            throw new ConfigException("Error reading resource " + resourceName, e);        }    }}