package oauth;import java.io.IOException;import io.github.cdimascio.dotenv.Dotenv;public class EnvClientConfig implements ClientConfigProvider{    private final Dotenv dotenv = Dotenv.configure().ignoreIfMissing().load();    private final String envVar;    public EnvClientConfig(String envVar) {        this.envVar = envVar;    }    @Override    public String loadClientJson() throws ConfigException, IOException {        String json = System.getenv(envVar);        if (json == null) {            json = dotenv.get(envVar);        }        if (json == null || json.isBlank()) {            throw new ConfigException(envVar + " not set or empty");        }        return json;    }}