package oauth;import java.nio.file.Files;import java.nio.file.Path;public class FileClientConfig implements ClientConfigProvider{    private final Path oauth2ClientIdFilePath;    public FileClientConfig(Path oauth2ClientIdFilePath) {        this.oauth2ClientIdFilePath = oauth2ClientIdFilePath;    }    @Override    public String loadClientJson() throws ConfigException {        try {            return Files.readString(oauth2ClientIdFilePath);        } catch (Exception e) {            throw new ConfigException("Cannot read client oauth2 client credentials file", e);        }    }}