package oauth;import com.google.auth.Credentials;import io.github.cdimascio.dotenv.Dotenv;import java.io.IOException;import java.util.Arrays;import java.util.List;public class Main {    public static void main(String[] args) throws AuthException, ConfigException, IOException, StorageException {        List<String> SCOPES = Arrays.asList(                            "https://www.googleapis.com/auth/script.projects",                            "https://www.googleapis.com/auth/script.scriptapp",                            "https://www.googleapis.com/auth/spreadsheets"            );            ClientConfigProvider clientConfig = new EnvClientConfig("OAUTH_CLIENT_JSON");        TokenStorage tokenStorage = new EnvTokenStorage("OAUTH_TOKEN_JSON", false);        Authenticator authenticator = new OauthEnvAuthenticator(clientConfig, tokenStorage, SCOPES);        Credentials creds = authenticator.authenticate();        System.out.printf("Access token: {}", creds);    }}